import * as React from "react";
import { useState, useEffect } from "react";
import {
  Box,
  Grid,
  Typography,
  List,
  ListItem,
  TextField,
  FormControl,
  Button,
  Chip,
} from "@mui/material";

const fieldStyle = {
  background: "white",
};

//Declare student list as an empty array
const StudentList = () => {
  const [studentNames, setStudentNames] = useState([]);
  //set local storage items as student names
  useEffect(() => {
    localStorage.setItem("studentNames", studentNames);
  });

  //dummy list for student names for testing
  // let studentNames = [
  //   "Mary Smith",
  //   "Joaquim Tochtermann",
  //   "Ahmed Mohammed",
  //   "Jules Reynolds",
  //   "Tuan Hong",
  //   "Bob Ray",
  //   "Liam Neeson",
  //   "Jose Gallo",
  //   "Nicole Herman",
  // ];

  //initialize random student variable
  let randomStudent;

  // TODO: Create function to map over student names after seeds are ready
  const handleGenerate = () => {
    console.log("The generate random button is working!");
    randomStudent =
      studentNames[Math.floor(Math.random() * studentNames.length)];
  };

  const [formState, setFormState] = useState({
    studentName: "",
  });

  //When user enters a name into the 
  const handleFormChange = (event) => {
    const { name, value } = event.target;

    setFormState({
      ...formState,
      [name]: value,
    });
  };

  const handleAddName = async (event) => {
    event.preventDefault();

    console.log(formState);
    console.log("The add name button is working!");

    setStudentNames((studentNames) => studentNames.concat());
  };

  console.log(studentNames);

  return (
    <Box>
      <FormControl>
        <TextField
          id="student-name-form"
          variant="outlined"
          label="Write a name"
          color="primary"
          onChange={(e) => handleFormChange(e.target.value)}
          sx={fieldStyle}
        ></TextField>
        <p>Enter a student's name and click "ADD NAME TO LIST".</p>
        <Button
          id="add-name-button"
          variant="contained"
          onClick={() => handleAddName()}
        >
          Add name to list
        </Button>
        <p>To select a random name, click "GENERATE A RANDOM NAME".</p>
        <Button
          id="generate-button"
          variant="contained"
          onClick={() => handleGenerate()}
        >
          Generate a random name
        </Button>
        <Chip
          variant="outlined"
          label={randomStudent}
          sx={{ mt: 4, mb: 2 }}
          color="primary"
        />
      </FormControl>

      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <Typography sx={{ mt: 4, mb: 2 }} variant="h6" component="div">
            <List id="student-name-list">
              {studentNames.map((student) => {
                return <ListItem key={student}>{student} </ListItem>;
              })}
            </List>
          </Typography>
        </Grid>
      </Grid>
    </Box>
  );
};
export default StudentList;
