import "./App.css";
import { TextField, FormControl, Button } from "@mui/material";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";
import StudentList from "./components/StudentList";

const theme = createTheme({
  typography: {
    fontFamily: '"Comfortaa", Arial, sans-serif',
    h1: {
      fontSize: "3.5rem",
      fontWeight: "bolder",
    },
    h2: {
      fontSize: "1.5em",
    },
    h3: {
      fontSize: "1em",
    },
    h6: {
      fontSize: "1em",
      textAlign: "center",
    },
    p: {
      fontSize: "1em",
      textAlign: "center",
    },
  },
  // palette: {
  //   primary: {
  //     light: "#ECF6FC", // background light blue
  //     main: "#105F9A", // theme blue
  //     dark: "#168BE2", // bright blue
  //     contrastText: "#FFFFFF",
  //   },
  //   secondary: {
  //     light: "#597992", // disabled blue
  //     main: "#062740", // dark blue
  //     accent: "#F5E410", // yellow
  //     contrastText: "#062740", // dark blue, text color
  //   },
  // },
});

const fieldStyle = {
  background: "white",
};

//Allows user to log initials and score, prints to page
function logScore(event) {
  event.preventDefault();
  if (document.getElementById("initials").value.length >= 4) {
      alert("Please write three or fewer characters.")
      return;
  }
  document.getElementById("timer").style.display = "none";
  //User high score and initials are stored and available on page load
  var highScore = {
      score: count,
      initials: document.getElementById("initials").value
  }
  if (!highScores) {
      highScores = [];
  }
  //hides form after score is logged to console to prevent multiple entries
  document.getElementById("initial-form").style.display = "none";
  //shows high scores from all previous games saved
  document.getElementById("high-scores").style.display = "block";
  //displays button to retake quiz
  document.getElementById("retake-quiz").style.display = "inline-block";

  highScores.push(highScore);

  //User high score and initials are displayed on page after 'SUBMIT' is clicked inside the "score-list" ol

  //Make a list item per user submission and display on page

  for (let index=0; index < highScores.length; index++) {
      var currentHighScore = highScores[index];
      var userScore = document.createElement("li");
      userScore.innerHTML = currentHighScore.initials + " : " + currentHighScore.score;
      document.getElementById("score-list").appendChild(userScore);

  }

  localStorage.setItem("highScores", JSON.stringify(highScores));
}
function reloadPage() {
  reload = location.reload();
}


function App() {
  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <Navbar />
        <header className="App-header">
          <img
            src="/images/mylogo.png"
            className="App-logo"
            alt="logo"
            height="20px"
            padding="10px"
          />
          <FormControl>
            <TextField
              id="student-name-form"
              variant="outlined"
              label="Write a name"
              color="primary"
              sx={fieldStyle}
            ></TextField>
            <p>Enter a student's name and click "GENERATE"</p>
            <Button variant="contained"> Generate </Button>
          </FormControl>
          <StudentList />
        </header>
        <Footer />
      </div>
    </ThemeProvider>
  );
}

export default App;
